<script>
import axios from 'axios'

// let refresh = false

// axios.interceptors.response.use(resp => resp, async error => {
//     if (error.response.status === 400 && !refresh) {
//         const {data} = await axios.get('cart')
//         localStorage.setItem('cartKey', data.key)
//         refresh = true
//         axios.request(error.config)
//     } 
//     refresh = false
//     return error
// })

import { defineComponent } from 'vue'
import { Carousel, Slide, Navigation, Pagination } from 'vue3-carousel';

import webSiteUnderItems from '../models/site__samples/site__under/webSiteUnderItems.vue'
import webSiteBrands from '../models/site__samples/site__under/webSiteBrands.vue'
import webSiteUnderText from '../models/site__samples/site__under/webSiteUnderText.vue'
import webSiteAdvantage from '../models/site__samples/site__under/webSiteAdvantage.vue'
import webTopMenu from '../models/site__samples/webTopMenu.vue'
import webSiteTop from '../models/site__samples/webSiteTop.vue'
import webSiteLeft from '../models/site__samples/webSiteLeft.vue'
import webFooter from '../models/site__samples/webFooter.vue'
import webMobileWrap from '../models/site__samples/webMobileWrap.vue'
import webMainItem from '../models/webMainItem.vue'
import webCategory from '../models/webCategory.vue'

axios.defaults.baseURL = 'http://localhost:3000/api'

export default defineComponent({
    name: 'webMain',
    components: {
      webMainItem,
      webCategory,
      webSiteTop,
      webTopMenu,
      webSiteLeft,
      webFooter,
      webMobileWrap,
      webSiteAdvantage,
      webSiteUnderText,
      webSiteBrands,
      webSiteUnderItems,
      Carousel,
      Slide,
      Navigation,
      Pagination,
    },
    data() {
      return {
        items: [],
        categories: [],
        mobile__modal: false,
        category__list: false,
        all__category: false,
        currentSlideBanner: 0,
        currentSlideItem1: 0,
        currentSlideItem2: 0,
        currentSlideItem3: 0,
        showedCartPath: false,
        key: localStorage.getItem('cartKey'),
        breakpoints: {
          0: {
            itemsToShow: 2,
            snapAlign: 'start',
          },
          769: {
            itemsToShow: 3,
            snapAlign: 'start',
          },
          1024: {
            itemsToShow: 4,
            snapAlign: 'start',
          },
        },
        feedBackModal: false,
        feedBackName: '',
        feedBackPhone: '',
        thankYouModal: false,
      }
    },
    methods: {
      nextBanner() {
        if (this.currentSlideBanner < 3) {
          this.currentSlideBanner++
        }
      },
      prevBanner() {
        if (this.currentSlideBanner > 0) {
          this.currentSlideBanner--
        }
      },
      nextItem1() {
        this.currentSlideItem1++
      },
      prevItem1() {
        this.currentSlideItem1--
      },
      nextItem2() {
        this.currentSlideItem2++
      },
      prevItem2() {
        this.currentSlideItem2--
      },
      nextItem3() {
        this.currentSlideItem3++
      },
      prevItem3() {
        this.currentSlideItem3--
      },
      async getItems() {
        const {data} = await axios.get('product')
        this.items = data
      },
      async getCategories() {
        const {data} = await axios.get('category')
        this.categories = data
      },
      async sendFeedBack() {
            const response = await axios.post('feedback', {
                fullname: this.feedBackName,
                phone: this.feedBackPhone
            })
            if (response.status < 300) {
                this.feedBackModal = false
                this.thankYouModal = true
                this.feedBackName = ''
                this.feedBackPhone = ''
            }
        },
      showMobileModal() {
        this.mobile__modal = true
      },
      closeMobileModal() {
        this.mobile__modal = false
      },
      showCategoryList() {
        this.category__list = !this.category__list
      },
      showAllCategory() {
        this.all__category = !this.all__category
      },
      showCartPath() {
        this.showedCartPath = true
        setTimeout(() => {
          this.showedCartPath = false
        }, 2500)
      }
    },
    watch: {
      "$route.query": {
            deep: true,
            handler() {
                location.reload()
            }
        },
    },
    mounted() {
      this.getItems(),
      this.getCategories()
    }
})
</script>

